targets.query:
import "strings"

from(bucket: "monitoring")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) =>
    r._measurement == "system_metrics" and
    r._field == "cpu_percent_total"
  )
  |> map(fn: (r) => ({
    r with
    _field: strings.replaceAll(v: r.host, t: ".home.virtualelephant.com", u: "")
  }))
  |> yield(name: "cpu_percent_total_all_hosts_short")
